sys_variable_value.value:
answer = (function() {
    var inventory = new GlideRecord('cmdb_ci_computer');
    inventory.addQuery('name', current.variables.computer_model);
    inventory.query();
    if (inventory.next() && inventory.quantity > 0) {
        return 'yes'; // 任务将继续
    } else {
        return 'no'; // 任务将被跳过
    }
})();

