catalog_script_client.script:
//Variable Controller OnChange  (current_section)
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    if (newValue == '0' || newValue == '1') {
        //display variables for tab 1
        g_form.setDisplay('variable1_tab1', true);
        g_form.setDisplay('variable2_tab1', true);

        //variables that should not dipslay on tab 1
        g_form.setDisplay('variable1_tab2', false);
        g_form.setDisplay('variable2_tab2', false);

		g_form.setDisplay('variable1_tab3', false);
        g_form.setDisplay('variable2_tab3', false);

		g_form.setDisplay('variable1_tab4', false);
        g_form.setDisplay('variable2_tab4', false);

		g_form.setDisplay('variable1_tab5', false);
        g_form.setDisplay('variable2_tab5', false);

    }

    if (newValue == '2') {
        //display variables for tab 2
        g_form.setDisplay('variable1_tab2', true);
        g_form.setDisplay('variable2_tab2', true);

        //variables that should not display on tab 2
        g_form.setDisplay('variable1_tab5', false);
        g_form.setDisplay('variable2_tab5', false);

        g_form.setDisplay('variable1_tab4', false);
        g_form.setDisplay('variable2_tab4', false);

		g_form.setDisplay('variable1_tab3', false);
        g_form.setDisplay('variable2_tab3', false);

        g_form.setDisplay('variable1_tab1', false);
        g_form.setDisplay('variable2_tab1', false);
    }

    if (newValue == '3') {
        //display variables for tab 2	
        g_form.setDisplay('variable1_tab3', true);
        g_form.setDisplay('variable2_tab3', true);

        //variables that should not display on tab 2
        g_form.setDisplay('variable1_tab5', false);
        g_form.setDisplay('variable2_tab5', false);

        g_form.setDisplay('variable1_tab4', false);
        g_form.setDisplay('variable2_tab4', false);

        g_form.setDisplay('variable1_tab2', false);
        g_form.setDisplay('variable2_tab2', false);

        g_form.setDisplay('variable1_tab1', false);
        g_form.setDisplay('variable2_tab1', false);
    }

    if (newValue == '4') {
        //display variables for tab 2	
        g_form.setDisplay('variable1_tab4', true);
        g_form.setDisplay('variable2_tab4', true);

        //variables that should not display on tab 2
        g_form.setDisplay('variable1_tab5', false);
        g_form.setDisplay('variable2_tab5', false);

        g_form.setDisplay('variable1_tab3', false);
        g_form.setDisplay('variable2_tab3', false);

        g_form.setDisplay('variable1_tab2', false);
        g_form.setDisplay('variable2_tab2', false);

        g_form.setDisplay('variable1_tab1', false);
        g_form.setDisplay('variable2_tab1', false);
    }

    if (newValue == '5') {
        //display variables for tab 2	
        g_form.setDisplay('variable1_tab5', true);
        g_form.setDisplay('variable2_tab5', true);

        //variables that should not display on tab 2
        g_form.setDisplay('variable1_tab4', false);
        g_form.setDisplay('variable2_tab4', false);

        g_form.setDisplay('variable1_tab3', false);
        g_form.setDisplay('variable2_tab3', false);

        g_form.setDisplay('variable1_tab2', false);
        g_form.setDisplay('variable2_tab2', false);

        g_form.setDisplay('variable1_tab1', false);
        g_form.setDisplay('variable2_tab1', false);
    }

}
