sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    gs.info("runMailScript");
    // Add your code here
    var cc1 = current.requested_for; //getUniqueValue
    var grUser1 = new GlideRecord('sys_user');
    grUser1.get(cc1);
    email.addAddress("cc", grUser1.email + '', grUser1.name + '');
    var cc2 = current.assigned_to;
    var grUser2 = new GlideRecord('sys_user');
    grUser2.get(cc2);
    email.addAddress("cc", grUser2.email + '', grUser2.name + '');
	gs.info("END runMailScript");
})(current, template, email, email_action, event);
